{
    "_id": "tell-hmrc-something",
    "formName": "Tell hmrc something",
    "description": "joidsjfaoisdjf",
    "developmentPhase": "beta",
    "displayHMRCLogo": false,
    "emailTemplateId": "eitt_submission_confirmation",
    "authConfig": {
        "authModule": "anonymous"
    },
    "sections": [
        {
            "title": "Are you an individual or a business?",
            "presentationHint": "invisiblePageTitle",
            "fields": [
                {
                    "type": "choice",
                    "id": "applicantType",
                    "label": "Are you an individual or a business?",
                    "choices": [
                        "Individual",
                        "Business"
                    ]
                }
            ]
        },
        {
            "title": "What is your name?",
            "presentationHint": "invisiblePageTitle",
            "includeIf": "${applicantType = 0}",
            "fields": [
                {
                    "type": "text",
                    "id": "yourName",
                    "label": "What is your name?",
                    "shortName": "Full name",
                    "format": "text"
                }
            ]
        },
        {
            "title": "Do you have an National insurance number?",
            "presentationHint": "invisiblePageTitle",
            "includeIf": "${applicantType = 1}",
            "fields": [
                {
                    "type": "choice",
                    "id": "hasNINO",
                    "label": "Do you have an National insurance number?",
                    "format": "yesno",
                    "choices": [
                        "Yes",
                        "No"
                    ]
                }
            ]
        },
        {
            "title": "What is your National insurance number?",
            "presentationHint": "invisiblePageTitle",
            "includeIf": "${applicantType = 0}",
            "fields": [
                {
                    "id": "nino",
                    "type": "text",
                    "label": "What is your National insurance number?",
                    "helpText": "It’s on your National Insurance card, benefit letter, payslip or P60. For example, ‘QQ 12 34 56 C’.",
                    "toUpperCase": "true",
                    "format": "nino"
                }
            ]
        },
        {
            "title": "What is your business name?",
            "presentationHint": "invisiblePageTitle",
            "includeIf": "${applicantType = 1}",
            "fields": [
                {
                    "type": "text",
                    "id": "businessName",
                    "label": "What is your business name?",
                    "shortName": "Business name",
                    "format": "text"
                }
            ]
        },
        {
            "title": "Does ${businessName} have a VAT registration number?",
            "presentationHint": "invisiblePageTitle",
            "includeIf": "${applicantType = 1}",
            "fields": [
                {
                    "type": "choice",
                    "id": "hasVRN",
                    "label": "Does ${businessName} have a VAT registration number?",
                    "format": "yesno",
                    "choices": [
                        "Yes",
                        "No"
                    ]
                }
            ]
        },
        {
            "title": "What is the VAT registration number ${businessName}?",
            "presentationHint": "invisiblePageTitle",
            "includeIf": "${hasVRN = 0 && applicantType = 1}",
            "fields": [
                {
                    "id": "ukvrn",
                    "type": "text",
                    "label": "What is the VAT registration number ${businessName}?",
                    "helpText": "This is 9 numbers, sometimes with ‘GB’ at the start, for example 123456789 or GB123456789. You can find it on Anycompany’s VAT registration certificate.",
                    "shortName": "VRN",
                    "toUpperCase": "true",
                    "format": "ukVrn"
                }
            ]
        },
        {
            "title": "Cannot use this service",
            "includeIf": "${hasVRN = 1}",
            "continueIf": "false",
            "fields": [
                {
                    "type": "info",
                    "id": "noFormatInfoBox",
                    "label": "Information",
                    "infoType": "noformat",
                    "infoText": "This is infoType \"noformat\" and can be used for general page content. it can include *markdown* text to render [links to approved domains](/submissions/new-form/superForm2019) and:\n* render things\n* like lists\n"
                }
            ]
        },
        {
            "title": "What is your email address?",
            "presentationHint": "invisiblePageTitle",
            "fields": [
                {
                    "type": "text",
                    "id": "email",
                    "label": "What is your email address?",
                    "helpText": "We may need to contact you as the person who submitted the application.",
                    "errorMessage": "This is not an email",
                    "shortName": "Email address",
                    "format": "text"
                }
            ]
        }
    ],
    "summarySection": {
        "title": "Check your answers before sending your application",
        "header": "",
        "footer": "<p class='govuk-!-margin-top-6'><a href ='/submissions/summary/pdf/${form.id}'> Save or print a PDF copy of your answers (opens in new tab)</a></p> <h2 class='govuk-heading-m'>Declaration</h2><p class='govuk-body'>By submitting this application you confirm that to the best of your knowledge the details you are providing are correct.</p>",
        "continueLabel": "Accept and submit"
    },
    "acknowledgementSection": {
        "title": "Acknowledgement Page",
        "fields": [
            {
                "id": "nextSteps",
                "type": "info",
                "label": "",
                "infoType": "noformat",
                "infoText": "We sent a confirmation email to **${email}** with your submission reference. The email does not include a copy of your answers. \n\n[Print or save a PDF copy of your form](${link.printAcknowledgementPdf})\n\n ## What happens next\n\n We aim to process your claim within 30 working days. We will send you written confirmation when this is done.\n\n Please allow an additional 10 days for any repayment to be made."
            }
        ]
    },
    "destinations": [
        {
            "id": "attendedDmsQueueDestination",
            "type": "hmrcDms",
            "failOnError": true,
            "convertSingleQuotes": true,
            "dmsFormId": "TBD",
            "customerId": "${'name'}",
            "classificationType": "TBD",
            "businessArea": "TBD"
        }
    ]
}
